import React from 'react'

export default class Login extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            usrid:"",   
            pwd:"",
            usr: {},
            loginerror: "",
            msg:""
        }
    }

    handleInput = (element )=> {      
        var key = element.target.name;    
        var val = element.target.value;
        this.setState({[key]: val});
    }

    submitForm=(ev)=>{
        ev.preventDefault();
        console.log(this.state);
        const reqData = {
            method: 'GET',
            headers: {
                'Content-Type':'application/json'
            },
            body: JSON.stringify({
                usrid: this.state.usrid,
                pwd: this.state.pwd,
            })
        }
        fetch("http://localhost:8081/login", reqData)
        .then(resp=>resp.text())
        .then(data=>{if(data.length != 0)
        {
            const json = JSON.parse(data);
            this.setState({usr: json});
            this.setState({msg:"Welcome, "+this.state.usr})
            localStorage.setItem("loggedinuser", JSON.stringify(this.state.usr));
        }
        else {
            this.setState({loginerror: "Wrong ID/Pwd"});
        }
    })
    }

    render(){
        return(
            <div className="container">
                <form>
                <h3>Sign In</h3>
                <div className="form-group">
                    <label>User ID</label>
                    <input type="text" name="usrid" className="form-control" placeholder="Enter User ID" onChange={this.handleInput} />
                </div><br/>
                <div className="form-group">
                    <label>Password</label>
                    <input type="password" name="pwd" className="form-control" placeholder="Enter password" onChange={this.handleInput}/>
                </div><br/>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary btn-block" onClick={this.submitForm}>Submit</button>
                </div>
                <br/><br/>
            </form>
            <h1>{this.state.msg}</h1>
            <h1>{this.state.loginerror}</h1>
            </div>
        )
    }
}
